import { React, useState, useRef, useCallback } from "react";
import { Camera } from "react-camera-pro";
import btnCamera from "../assets/img/btn-camera.svg";


// const videoConstraints = {
//   width: 390,
//   height: 520,
//   facingMode: "environment"
// };

const TakePic = () => {
  const [isCaptureEnable, setCaptureEnable] = useState(true);
  const camera = useRef(null);
  const [url, setUrl] = useState(null);

  const takePhoto = useCallback(() => {
    const imageSrc = camera.current?.takePhoto();
    if (imageSrc) {
      setUrl(imageSrc);
      setCaptureEnable(false);
    }
  }, [camera])

  return (
    <>
      {isCaptureEnable || (
        <>

          <button onClick={() => setCaptureEnable(true)}>開始</button>
          <img src={url} alt='Taken photo' />
        </>
      )}
      {isCaptureEnable && (
        <>
          {/* <div>
            <button onClick={() => setCaptureEnable(false)}>終了</button>
          </div> */}
          <div>
            <Camera ref={camera} aspectRatio={9 / 16} />
            <img src={btnCamera} alt="" onClick={takePhoto} />
          </div>
        </>
      )}
    </>
  );
}

export default TakePic;
